script:
frappe.ui.form.on('Internal Audit Details', {
	refresh(frm) {
		if(frm.is_new()){
		    frm.set_df_property('audit_start_date', 'hidden', 1);
		    frm.set_df_property('audit_end_date', 'hidden', 1);
		    frm.set_df_property('actual_auditees_and_auditors_section', 'hidden', 1);
		}
	},
	before_save(frm){
	    let flag = false;
	    frm.doc.planned_auditors.forEach(function (ch){
	        if (ch.is_auditor_team_lead === 1){
	            flag = true;
	        }
	    });
	    if(flag === false){
	        frappe.throw("Add atleast one Auditor Team Lead");
	    }
	}
});
